import React, { useState } from 'react';

interface DropdownProps {
  options: string[];
  onSelect: (selectedOption: string) => void;
}

function Dropdown({ options, onSelect }: DropdownProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState('');

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option: string) => {
    setSelectedOption(option);
    onSelect(option);
    setIsOpen(false);
  };

  return (
    <div className="relative">
    <button 
      onMouseEnter={toggleDropdown} 
      onMouseLeave={toggleDropdown} 
      className="border border-gray-400 px-4 py-2 rounded-md mr-2 flex items-center"
    >
      {selectedOption || 'Select an option'}
      <svg 
        xmlns="http://www.w3.org/2000/svg" 
        className={`h-5 w-5 ml-2 ${isOpen ? 'transform rotate-180' : ''}`} 
        viewBox="0 0 20 20" 
        fill="currentColor"
      >
        <path 
          fillRule="evenodd" 
          d="M10 18a.75.75 0 0 1-.53-.22l-7-7a.75.75 0 1 1 1.06-1.06L10 16.94l6.47-6.47a.75.75 0 1 1 1.06 1.06l-7 7A.75.75 0 0 1 10 18z" 
        />
      </svg>
    </button>
    {isOpen && (
      <div 
        className={`absolute bg-white border border-gray-400 rounded-md shadow-md mt-2 md:mt-0 w-full md:w-auto`}
        style={{ left: isOpen ? 0 : '-9999px' }}
      >
        {options.map(option => (
          <div
            key={option}
            onClick={() => handleOptionClick(option)}
            className="cursor-pointer px-4 py-2 hover:bg-gray-100"
          >
            {option}
          </div>
        ))}
      </div>
    )}
  </div>
  

  );
}

export default Dropdown;
