import { useState } from 'react';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import Dropdown from './components/Dropdown';
import { Task } from './types';

function App() {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [selectedPriority, setSelectedPriority] = useState<string>('');

  const addTask = (task: Task) => {
    setTasks([...tasks, task]);
  };

  const deleteTask = (taskId: number) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const priorityOptions = ['High', 'Medium', 'Low'];

  const handlePrioritySelect = (priority: string) => {
    setSelectedPriority(priority);
  };

  return (

   <div className="container mx-auto p-4 grid grid-rows-1 md:grid-rows-3 md:gap-4">

  <div className="row-span-1 md:row-span-1">
    <h1 className="text-3xl font-bold mb-4">Task Manager</h1>
  </div>
  <div className="row-span-1 md:row-span-1 flex flex-col md:flex-row md:justify-between md:items-center">
    <AddTaskForm addTask={addTask} />
    <Dropdown options={priorityOptions} onSelect={handlePrioritySelect} />
    <span className="text-gray-600 md:mb-0">{selectedPriority && `Selected Priority: ${selectedPriority}`}</span>
  </div>  
  <div className="row-span-1 md:row-span-1">
    <TaskList tasks={tasks} deleteTask={deleteTask} />
  </div>
</div>


  );
}

export default App;
